list.expr:
group by (node, pod) (
  changes(
      sum without (uid, pod_ip)(
          kube_pod_info{pod=~"worker-.*", node!=""}
      )
  ) > 0
  and on (pod) 
  sum by (pod)(
      kube_pod_info{pod=~"worker-.*", node!=""}
  ) @ start()
)

targets.expr:
with (
  filters = {job_id=~"$slurm_job"}
)
slurm_job_info{filters}
* on (job_id) group_left()
count by (job_id) (slurm_node_job{filters})
